import Paper from "@material-ui/core/Paper";
import { Form, Field } from "react-final-form";
import TextField from "@material-ui/core/TextField";

import UserFormStyles from "../styles/UserForm.module.scss";

const styles = {
  MuiPaper: {
    background: "#FFFFFF",
    padding: 45,
    boxSizing: "border-box",
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.15)",
    borderRadius: 10,
  },
};

const fields = [
  { id: 1, name: "userName", label: "Фамилия и имя", iconPath: "/badge.png" },
  { id: 2, name: "email", label: "E-mail", iconPath: "/mail.png" },
  {
    id: 3,
    name: "phoneNumber",
    label: "Номер телефона",
    iconPath: "/phone.png",
  },
];

export default function UserForm() {
  const handleFormSubmit = () => console.log("handleFormSubmit");

  const renderUserForm = (fields) =>
    fields.map((field) => (
      <Field
        name={field.name}
        render={() => (
          <div className={UserFormStyles.FieldItem}>
            <label for={field.id}>
              <img
                src={field.iconPath}
                className={UserFormStyles.FieldIcon}
                alt={field.label}
              />
            </label>
            <TextField id={field.id} label={field.label} variant="outlined" />
          </div>
        )}
      />
    ));

  return (
    <Paper style={styles.MuiPaper}>
      <Form
        onSubmit={handleFormSubmit}
        // validate={validate}
        render={({ handleSubmit }) => (
          <form onSubmit={handleSubmit}>
            <section className={UserFormStyles.MainFormContent}>
              {renderUserForm(fields)}
            </section>

            <button type="submit" className={UserFormStyles.SubmitButton}>
              Сохранить изменения
            </button>
          </form>
        )}
      />
    </Paper>
  );
}
