import Paper from "@material-ui/core/Paper";
import { Form, Field } from "react-final-form";
import TextField from "@material-ui/core/TextField";

import UserFormStyles from "../styles/UserForm.module.scss";

const styles = {
  MuiPaper: {
    background: "#FFFFFF",
    padding: 45,
    boxSizing: "border-box",
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.15)",
    borderRadius: 10,
  },
};

const fields = [
  { id: 1, name: "userName", label: "Фамилия и имя", iconPath: "/badge.png" },
  { id: 2, name: "email", label: "E-mail", iconPath: "/mail.png" },
  {
    id: 3,
    name: "phoneNumber",
    label: "Номер телефона",
    iconPath: "/phone.png",
  },
];

export default function UserForm() {
  const handleFormSubmit = () => console.log("handleFormSubmit");

  const renderUserForm = (fields) =>
    fields.map((field) => (
      <div>
        <label>
          <img src={field.iconPath} alt={field.label} />
        </label>
        <Field
          name={field.name}
          conponent={<TextField label={label} variant="outlined" />}
        />
      </div>
    ));

  return (
    <Paper style={styles.MuiPaper}>
      <Form
        onSubmit={handleFormSubmit}
        // validate={validate}
        render={({ handleSubmit }) => (
          <form onSubmit={handleSubmit}>
            <section className={UserFormStyles.MainFormContent}>
              <div>
                <label>
                  <img src="/badge.png" alt="user name" />
                </label>
                <Field
                  name="userName"
                  render={() => UserInfoField("Фамилия и имя")}
                />
              </div>

              <div>
                <label>Interests</label>
                <Field name="interests" component={TextField} />
              </div>

              <Field
                name="bio"
                render={({ input, meta }) => (
                  <div>
                    <label>Bio</label>
                    <textarea {...input} />
                    {meta.touched && meta.error && <span>{meta.error}</span>}
                  </div>
                )}
              />

              <h2>Render Function as Children</h2>
              <Field name="phone">
                {({ input, meta }) => (
                  <div>
                    <label>Phone</label>
                    <input type="text" {...input} placeholder="Phone" />
                    {meta.touched && meta.error && <span>{meta.error}</span>}
                  </div>
                )}
              </Field>
            </section>

            <button type="submit">Submit</button>
          </form>
        )}
      />
    </Paper>
  );
}
