import Paper from "@material-ui/core/Paper";
import { Form, Field } from "react-final-form";
import TextField from "@material-ui/core/TextField";

import UserFormStyles from "../styles/UserForm.module.scss";

const styles = {
  MuiPaper: {
    background: "#FFFFFF",
    padding: 45,
    boxSizing: "border-box",
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.15)",
    borderRadius: 10,
  },
};

const fields = [
  { id: 1, label: "Фамилия и имя", icon: "/badge.png" },
  { id: 2, label: "E-mail", icon: "/mail.png" },
  { id: 3, label: "Номер телефона", icon: "/phone.png" },
];

export default function UserForm() {
  const handleFormSubmit = () => console.log("handleFormSubmit");

  const UserInfoField = (label) => (
    <TextField label={label} variant="outlined" />
  );

  return (
    <Paper style={styles.MuiPaper}>
      <Form
        onSubmit={handleFormSubmit}
        // validate={validate}
        render={({ handleSubmit }) => (
          <form onSubmit={handleSubmit}>
            <section className={UserFormStyles.MainFormContent}>
              <div>
                <label>
                  <img src="/badge.png" alt="user name" />
                </label>
                <Field
                  name="userName"
                  render={() => UserInfoField("Фамилия и имя")}
                />
              </div>

              <div>
                <label>Interests</label>
                <Field name="interests" component={TextField} />
              </div>

              <Field
                name="bio"
                render={({ input, meta }) => (
                  <div>
                    <label>Bio</label>
                    <textarea {...input} />
                    {meta.touched && meta.error && <span>{meta.error}</span>}
                  </div>
                )}
              />

              <h2>Render Function as Children</h2>
              <Field name="phone">
                {({ input, meta }) => (
                  <div>
                    <label>Phone</label>
                    <input type="text" {...input} placeholder="Phone" />
                    {meta.touched && meta.error && <span>{meta.error}</span>}
                  </div>
                )}
              </Field>
            </section>

            <button type="submit">Submit</button>
          </form>
        )}
      />
    </Paper>
  );
}
