import Paper from "@material-ui/core/Paper";
import { Form, Field } from "react-final-form";
import TextField from "@material-ui/core/TextField";
import { useMachine } from "@xstate/react";
import { Machine } from "xstate";

import UserFormStyles from "../styles/UserForm.module.scss";
import { userInfoConfig } from "../xstate/userInfo.js";

const styles = {
  MuiPaper: {
    background: "#FFFFFF",
    padding: 45,
    boxSizing: "border-box",
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.15)",
    borderRadius: 10,
  },
  TextField: {
    height: 57,
    width: 254,
    left: 0,
  },
};

const fields = [
  { id: 0, name: "userName", label: "Фамилия и имя", iconPath: "/badge.png" },
  { id: 1, name: "email", label: "E-mail", iconPath: "/mail.png" },
  {
    id: 2,
    name: "phoneNumber",
    label: "Номер телефона",
    iconPath: "/phone.png",
  },
];

export default function UserForm() {
  const userInfo = Machine(userInfoConfig);
  const [info, send] = useMachine(userInfo);

  const handleFormSubmit = (values) => console.log("values", values);

  const renderUserForm = (fields) =>
    fields.map((field) => (
      <React.Fragment key={field.id}>
        <Field
          name={field.name}
          style={styles.TextField}
          render={() => (
            <div className={UserFormStyles.FieldItem}>
              <label htmlFor={field.id}>
                <img
                  src={field.iconPath}
                  className={UserFormStyles.FieldIcon}
                  alt={field.label}
                />
              </label>
              <TextField
                id={field.name}
                label={field.label}
                style={styles.TextField}
                variant="outlined"
              />
            </div>
          )}
        />
        {fields.length - 1 !== field.id ? (
          <div className={UserFormStyles.Divider} />
        ) : null}
      </React.Fragment>
    ));

  return (
    <Paper style={styles.MuiPaper}>
      <Form
        onSubmit={handleFormSubmit}
        // send({ type: "SUBMIT" })
        // validate={validate}
        render={({ handleSubmit, values }) => (
          <form onSubmit={handleSubmit} className={UserFormStyles.UserForm}>
            <section className={UserFormStyles.MainFormContent}>
              {renderUserForm(fields)}
            </section>

            <button type="submit" className={UserFormStyles.SubmitButton}>
              Сохранить изменения
            </button>
            {/* <p className={UserFormStyles.SuccessMessage}>
              Изменения сохранены!
            </p> */}
          </form>
        )}
      />
    </Paper>
  );
}
