import { assign } from "xstate";

export const userInfoConfig = {
  id: "saveChanges",
  context: {
    name: "",
    results: [],
  },
  initial: "ready",
  states: {
    ready: {
      on: {
        INPUT_NAME: {
          actions: assign((ctx, evt) => ({
            name: evt.name,
          })),
        },
        SUBMIT: {
          target: "searching",
        },
      },
    },
    error: {},
    searching: {},
    success: {},
  },
};
